import React, { useEffect, useState } from "react";
import Item from "./DisplayTeddies";
import axios from "axios";

const URL = "http://localhost:5000/items"; // Ensure correct URL

const fetchHandler = async () => {
    try {
        const response = await axios.get(URL); // Use .get method for GET requests
        return response.data; // Return the data
    } catch (error) {
        console.error("Error fetching data:", error); // Log errors
        return { items: [] }; // Return an empty array in case of error
    }
};

function TeddyBares(props) {
    const { itemname, getprice, sellprice, pieces, date } = props.item || {}; // Ensure props.item is defined

    const [items, setItems] = useState([]); // Initialize with empty array

    useEffect(() => {
        const loadItems = async () => {
            const data = await fetchHandler();
            setItems(data.items || []); // Ensure items is an array
        };
        loadItems();
    }, []);

    return (
        <div>
            <div>
                {items.length > 0 ? (
                    items.map((item, i) => (
                        <div key={i}>
                            <Item item={item} />
                        </div>
                    ))
                ) : (
                    <p>No items found.</p> // Provide feedback when no items are available
                )}
            </div>
            <h1>{itemname}</h1>
            <h1>{getprice}</h1>
            <h1>{sellprice}</h1>
            <h1>{pieces}</h1>
            <h1>{date}</h1>
        </div>
    );
}

export default TeddyBares;
