import React, { useEffect, useState } from "react";
import axios from 'axios';
import "./DisplayInventory.css";

import NavBar from "../../Components/Nav/Nav";
import Footer from "../../Components/Nav/Footer"
import { Link, useNavigate } from "react-router-dom";  

const URL = "http://localhost:5000/items";

const fetchHandler = async () => {
    try {
        const response = await axios.get(URL);
        return response.data;
    } catch (error) {
        console.error("Error fetching data:", error);
        return { items: [] }; 
    }
}

function DisplayInventory(props) {
    const {_id,itemname,getprice,sellprice,pices,date} =props.item;
    const [items, setItems] = useState([]);

    useEffect(() => {
        fetchHandler().then((data) => setItems(data.items || []));
    }, []);

    const history = useNavigate();

    const deleteHandler = async()=>{
        await axios.delete(`http://localhost:5000/items/${_id}`)
        .then(res=>res.data)
        .then(()=>history("/"))
        .then(()=>history("/Inventory"))
    }

    return (
        <div className="Site">
            <NavBar/>
            <div className="inventory">
                <div className="table">
                    <table>
                        <thead>
                            <tr>
                                <th>No</th>
                                <th>Item Name</th>
                                <th>Get Price</th>
                                <th>Sell Price</th>
                                <th>Pices</th>
                                <th>Date</th>
                            </tr>
                        </thead>
                        <tbody>
                            {items.map((item, index) => (
                                <tr key={index}>
                                    <td>{item._id}</td>
                                    <td>{item.itemname}</td>
                                    <td>{item.getprice}</td>
                                    <td>{item.sellprice}</td>
                                    <td>{item.pices}</td>
                                    <td>{item.date}</td>
                                    <td><button  onClick={deleteHandler} className="delete "type="delete">Delete</button> 
                                    <Link to ={`/Inventory/${item._id}`}>Update</Link></td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                   
                </div>
            </div>
            <Footer/>
        </div>
    );
}

export default DisplayInventory;
