import React, { useState } from "react";

import './additems.css';
import axios from 'axios';


function additems() {
  const history = useNavigate();
  const [inputs,setInputs] = useState({
    itemname:"",
    getprice:"",
    sellprice:"",
    pices:"",
    date:"",
    
  });
  const handleChange =(e)=>{
    setInputs((prevState)=>({
      ...prevState,
      [e.target.name]:e.target.value,
    }))
  }
  const handleSubmit =(e)=>{
    e.preventDefualt();
    console.log(inputs);
    sendRequest().then(()=>history(''))
  }
   const sendRequest = async()=>{
    await axios.post("http://Localhost: 5000/items",{
      itemname : String(inputs.itemname),
      getprice : String(inputs.getprice),
      sellprice : String(inputs.sellprice),
      pices : String(inputs.pices),
      date : String(inputs.date),
    }).then(res => res.data);
   }
    
  return (
    <div className="wrapper">
      <div className="images-details ">
        <div className="image ">
          <img src="./Images/panda.jpg"></img>
        </div>
        <div className="details">
          <form action="" class="contact">
            <h4>Name : </h4>
            <input type="text" name="itemname" value={inputs.itemname} ></input>
            
            <h4> Get price : </h4>
            <input type="text" name="getprice" value={inputs.getprice} ></input>
            <h4> Sell Price : </h4>
            <input type="text" name="sellprice"  value={inputs.sellprice}></input>
            <h4> Pices : </h4>
            <input type="number" name="pices" value={inputs.pices}></input>
            <h4> Date : </h4>
            <input type="date" name="date" value={inputs.date}></input>

            <button type="sumbit">Submit</button>
           
          </form>
        </div>
      </div>
    </div>
  );
}

export default additems;
