import React, { useState } from "react";
import { useNavigate } from "react-router";

import "./additems.css";
import axios from "axios";

function AddItems() {
  const history = useNavigate();
  const [inputs, setInputs] = useState({
    itemname: "",
    getprice: "",
    sellprice:"",
    pices:"",
    date:""
  });
  const handleChange = (e) => {
    setInputs((prevState) => ({
      ...prevState,
      [e.target.itemname]: e.target.value,
    }));
  };
  const handleSubmit = (e) => {
    e.preventDefualt();
    console.log(inputs);
    sendRequest().then(()=>history('DisplayItems'))
    
  };
  
  const sendRequest = async () => {
    await axios
      .post("http://Localhost:5000/items", {
        itemname: String(inputs.itemname),
        getprice: Number(inputs.getprice),
        sellprice: Number(inputs.sellprice),
        pices: Number(inputs.pices),
        date: String(inputs.date),
       
      })
      .then((res) => res.data);
  };

  return (
    <div className="wrapper">
      <div className="images-details ">
        <div className="image ">
          <img src="./Images/panda.jpg"></img>
        </div>
        <div className="details">
          <form  action="" class="contact">
            <h4>Name : </h4>
            <input
              type="text"
              name="itemname"
              onChange={handleChange}
              value={inputs.itemname}
            ></input>

            <h4> Get price : </h4>
            <input
              type="Number"
              name="getprice"
              onChange={handleChange}
              value={inputs.getprice}
            ></input>
            <h4> Sell Price : </h4>
            <input
              type="Number"
              name="sellprice"
              onChange={handleChange}
              value={inputs.sellprice}
            ></input>
            <h4> Pices : </h4>
            <input
              type="Number"
              name="pices"
              onChange={handleChange}
              value={inputs.pices}
            ></input>
            <h4> Date : </h4>
            <input
              type="text"
              name="date"
              onChange={handleChange}
              value={inputs.date}
            ></input>
          
            <button type="sumbit" onSubmit={handleChange}>Submit</button>
            
          </form>
        </div>
      </div>
    </div>
  );
}

export default AddItems;
