import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';

function UpdateItems() {
    const [inputs, setInputs] = useState({
        itemname: '',
        getprice: '',
        sellprice: '',
        pices: '',
        date: ''
    });
    const history = useNavigate();
    const { id } = useParams();

    useEffect(() => {
        const fetchHandler = async () => {
            try {
                const response = await axios.get(`http://localhost:5000/items/${id}`);
                setInputs(response.data.user);
            } catch (error) {
                console.error("Error fetching data:", error);
            }
        };
        fetchHandler();
    }, [id]);

    const sendRequest = async () => {
        try {
            await axios.put(`http://localhost:5000/items/${id}`, {
                itemname: String(inputs.itemname),
                getprice: Number(inputs.getprice),
                sellprice: Number(inputs.sellprice),
                pices: Number(inputs.pices),
                date: String(inputs.date),
            });
        } catch (error) {
            console.error("Error updating item:", error);
        }
    };

    const handleChange = (e) => {
        setInputs(prevState => ({
            ...prevState,
            [e.target.name]: e.target.value,
        }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log(inputs);
        sendRequest().then(() => history('/Inventory'));
    };

    return (
        <div>
            <form onSubmit={handleSubmit}>
                <h4>Upload Teddy Image :</h4><br/>
                <input type='file' name="img" />
                <h4>Item Name :</h4><br/>
                <input type='text' name="itemname" onChange={handleChange} value={inputs.itemname} />
                <h4>Get Price :</h4>
                <input type='text' name="getprice" onChange={handleChange} value={inputs.getprice} />
                <h4>Sell Price :</h4><br/>
                <input type='text' name="sellprice" onChange={handleChange} value={inputs.sellprice} />
                <h4>Pices :</h4><br/>
                <input type='text' name="pices" onChange={handleChange} value={inputs.pices} />
                <h4>Date :</h4><br/>
                <input type='text' name="date" onChange={handleChange} value={inputs.date} /><br/>
                <button type='submit'>Submit</button>
            </form>
        </div>
    );
}

export default UpdateItems;
