import React, { useEffect, useState } from 'react';

import Items from "../Items/Item"
import axios from 'axios';

const URL ="http://Localhost:5000/items";

const fetchHandler =  async()=>{

    
    return await axios(URL).then((res)=> res.data);
}//url ek use kkrl data tika argen innw iseelma


function Displayitems(props) {
    const[_id,name,price] = props.item;
    const[items,setItems]= useState();//usestatse use krl items tika set krn innw variable ekkt

    useEffect(()=>{
        fetchHandler().then((data)=>setItems(data.items));
    },[])

  return (
    <div>
      <h1>Details</h1>
      <div>
        {items &&  items.map((item,i)=>(
            <div key={i}>
                <Items item={item}/>
                </div>
                
        ))}
        

      </div>
    </div>
  )
}

export default Displayitems
